package com.javaex.practice;

public class Ex04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x, y;
		
		x = 10;
		y = 20;
		
		int sum = x + y; // sum이라는 이름의 메모리의 변수값을 지정해야한다 x,y는 숫자이므로 int
						 // 10+20의 정수 이므로 정수형 변수 byte short int long 모두 가능
		
		System.out.println("합은"+ sum);
	}

}
